import React, {useState} from 'react';
import {Box, Grid, FilledInput, Select, MenuItem, Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography, makeStyles, IconButton, CircularProgress} from '@material-ui/core';
import {Close as CloseIcon} from '@material-ui/icons'

const useStyles = makeStyles((theme)=>({
    skillChip:
    {
        margin: theme.spacing(0.5),
        padding: theme.spacing(0.75),
        fontSize: "14.5px",
        borderRadius: "5px",
        fontWeight:"600",
        border: `1px solid ${theme.palette.secondary.main}`,
        color: theme.palette.secondary.main,
        cursor:"pointer",
        '&:hover':{
            backgroundColor: theme.palette.secondary.main,
            color:"#fff",
        }
    },
    included:{
        backgroundColor: theme.palette.secondary.main,
        color:"#fff",
    }
}))
 const initState = {
    title:'',
    type:'Full time',
    companyName:'',
    companyUrl:'',
    location:'Remote',
    link:'',
    description:'',
    skills:[],
}

export default (props) => {
    const [loading, setLoading]=useState(false);
    const classes = useStyles();
    const [jobDetails,setJobDetails] = useState(initState);

    const handleChange = e => {
        e.persist();
        setJobDetails(oldState => ({ ...oldState, [e.target.name]: e.target.value}))
    }
    const skills=["Javascript","React","Node","Vue","Firebase","MongoDB","SQL"]

    //console.log(jobDetails);
    const addRemoveSkill = skill => {
        jobDetails.skills.includes(skill)
        ? setJobDetails(oldState => ({ ...oldState, skills: oldState.skills.filter((s) => s!==skill)}))
        : setJobDetails(oldState => ({ ...oldState, skills: oldState.skills.concat(skill)}))
    };
    const handleSubmit = async () => {
        for (const field in jobDetails){
            if(typeof jobDetails[field] === 'string' && !jobDetails[field]) return;     
        }
        if(!jobDetails.skills.length)return;
        setLoading(true);
        await props.postJob(jobDetails);
        closeModal();
    };
    const closeModal = () => {
        setJobDetails(initState);
        setLoading(false);
        props.closeModal();
    };
    return(
        // <Dialog open={props.newJobModal} fullWidth>
        <Dialog open={true} fullWidth>
            <DialogTitle>
                <Box display="flex" justifyContent="space-between" alignItems="center">
                    Post Job
                    <IconButton onClick={closeModal}>
                        <CloseIcon/>
                    </IconButton>
                </Box>
                </DialogTitle>

            <DialogActions>
                <Box width="100%" 
                    display="flex" 
                    justifyContent="space-between" 
                    color="red" 
                    alignItems={"center"}>
                    <Typography variant="caption">*Required fields</Typography>
                    <Button onClick={handleSubmit} 
                            variant="contained" 
                            disableElevation color="primary"
                            disabled={loading}
                            >   
                            {loading ? (<CircularProgress color="secondary" size={22}/>)
                                     : ("Post job")}

                    </Button>
                </Box>
            </DialogActions>
        </Dialog>
    )
}
