import React, {useState} from 'react';
import { Box, Button, Select, MenuItem, makeStyles} from '@material-ui/core'

const useStyles = makeStyles({
    wrapper: {
        backgroundColor: "#fff",
        display: "flex",
        boxShadow: "1px 1px 5px rgba(0, 0, 0, 0.1)",
        borderRadius: "5px",
        "& > *":{
            flex:1,
            height:"45px",
            margin: "8px",
        }
    }
})

export default props =>{
    const classes = useStyles();
    const [loading, setLoading] = useState(false);
    const [jobsearch, setJobsearch] = useState({
        jobtype:'Full time',
        location:'In-office'
    })
    const handleChange = e => {
        e.persist();
        setJobsearch(oldState => ({ ...oldState, [e.target.name]: e.target.value}))
    }
    // console.log(jobsearch);
    return (
        <Box p={2} mt={-5} mb={2} className={classes.wrapper}>
            <Select onChange={handleChange} name="jobtype" value={jobsearch.jobtype} disableUnderline variant="filled">
                <MenuItem value="Full time">Full time</MenuItem>
                <MenuItem value="Part time">Part time</MenuItem>
                <MenuItem value="Contract">Contract</MenuItem>
            </Select>
            <Select onChange={handleChange} name="location" value={jobsearch.location} disableUnderline variant="filled">
                <MenuItem value="Remote">Remote</MenuItem>
                <MenuItem value="In-office">In-office</MenuItem>
            </Select>
            <Button variant ="contained" color="primary" disableElevation>
                Search</Button>
        </Box>
    )
}
