import React, {useState} from 'react';
import {Box, Grid, FilledInput, Select, MenuItem, Dialog, DialogTitle, DialogContent, DialogActions, Button, Typography, makeStyles, IconButton, CircularProgress, RadioGroup, Radio, FormControlLabel} from '@material-ui/core';
import {Close as CloseIcon} from '@material-ui/icons'

const useStyles = makeStyles((theme)=>({
    skillChip:
    {
        margin: theme.spacing(0.5),
        padding: theme.spacing(0.75),
        fontSize: "14.5px",
        borderRadius: "5px",
        fontWeight:"600",
        border: `1px solid ${theme.palette.secondary.main}`,
        color: theme.palette.secondary.main,
        cursor:"pointer",
        '&:hover':{
            backgroundColor: theme.palette.secondary.main,
            color:"#fff",
        }
    },
    included:{
        backgroundColor: theme.palette.secondary.main,
        color:"#fff",
    }
}))
 const initState = {
    title:'',
    type:'Full time',
    companyName:'',
    companyUrl:'',
    location:'Remote',
    link:'',
    description:'',
    skills:[],
    //jobTitle:'',
//companyName:'
//industry
//jobLocation
//remoteType
//experienceMin
//experienceMax
//salaryMin
//salaryMax
//totalEmployee
//applyType
}

export default (props) => {
    const [loading, setLoading]=useState(false);
    const classes = useStyles();
    const [jobDetails,setJobDetails] = useState(initState);

    const handleChange = e => {
        e.persist();
        setJobDetails(oldState => ({ ...oldState, [e.target.name]: e.target.value}))
    }
    const skills=["Javascript","React","Node","Vue","Firebase","MongoDB","SQL"]

    //console.log(jobDetails);
    const addRemoveSkill = skill => {
        jobDetails.skills.includes(skill)
        ? setJobDetails(oldState => ({ ...oldState, skills: oldState.skills.filter((s) => s!==skill)}))
        : setJobDetails(oldState => ({ ...oldState, skills: oldState.skills.concat(skill)}))
    };
    const handleSubmit = async () => {
        for (const field in jobDetails){
            if(typeof jobDetails[field] === 'string' && !jobDetails[field]) return;     
        }
        if(!jobDetails.skills.length)return;
        setLoading(true);
        await props.postJob(jobDetails);
        closeModal();
    };
    const closeModal = () => {
        setJobDetails(initState);
        setLoading(false);
        props.closeModal();
    };
    return(
         <Dialog open={props.newJobModal} fullWidth>
            <DialogTitle>
                <Box display="flex" justifyContent="space-between" alignItems="center">
                    Post Job
                    <IconButton onClick={closeModal}>
                        <CloseIcon/>
                    </IconButton>
                </Box>
                </DialogTitle>
                <DialogContent>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <Box mb={1}>
                            Job title *
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="jobTitle"
                            value={jobDetails.jobTitle}
                            autoComplete="off" 
                            placeholder="ex. UX UI Designer" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    <Grid item xs={12}>
                        <Box mb={1}>
                            Company name *
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="companyName"
                            value={jobDetails.companyName}
                            autoComplete="off" 
                            placeholder="ex. Google" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    <Grid item xs={12}>
                        <Box mb={1}>
                            Industry *
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="industry"
                            value={jobDetails.industry}
                            autoComplete="off" 
                            placeholder="ex. Information Technology" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    <Grid item xs={6}>
                        <Box mb={1}>
                            Location
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="jobLocation"
                            value={jobDetails.jobLocation}
                            autoComplete="off" 
                            placeholder="ex. Chennai" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    <Grid item xs={6}>
                        <Box mb={1}>
                            Remote type
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="remoteType"
                            value={jobDetails.remoteType}
                            autoComplete="off" 
                            placeholder="ex. In-office" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    <Grid item xs={6}>
                        <Box mb={1}>
                            Experience
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="experienceMin"
                            value={jobDetails.experienceMin}
                            autoComplete="off" 
                            placeholder="Minimum" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    <Grid item xs={6}>
                        <Box mt={3.3}>
                            <FilledInput 
                                onChange={handleChange}
                                name="experienceMax"
                                value={jobDetails.experienceMax}
                                autoComplete="off" 
                                placeholder="Maximum" 
                                disableUnderline fullWidth>
                            </FilledInput>
                        </Box>
                    </Grid>
                    <Grid item xs={6}>
                        <Box mb={1}>
                            Salary
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="salaryMin"
                            value={jobDetails.salaryMin}
                            autoComplete="off" 
                            placeholder="Minimum" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    <Grid item xs={6}>
                        <Box mt={3.3}>
                            <FilledInput 
                                onChange={handleChange}
                                name="salaryMax"
                                value={jobDetails.salaryMax}
                                autoComplete="off" 
                                placeholder="Maximum" 
                                disableUnderline fullWidth>
                            </FilledInput>
                        </Box>
                    </Grid>
                    <Grid item xs={12}>
                        <Box mb={1}>
                            Total Employee
                        </Box>
                        <FilledInput 
                            onChange={handleChange}
                            name="totalEmployee"
                            value={jobDetails.totalEmployee}
                            autoComplete="off" 
                            placeholder="ex. 100" 
                            disableUnderline fullWidth>
                        </FilledInput>
                    </Grid>
                    {/* <Grid item xs={12}>
                        <Box mb={1}>
                            Apply type
                        </Box>
                        <Select>
                            <MenuItem name="applyType" value="quickApply">Quick Apply</MenuItem>
                            <MenuItem name="applyType" value="externalApply">External Apply</MenuItem>
                        </Select>
                    </Grid> */}
                    <Grid item xs={12}>
                    <RadioGroup
                            row
                            // defaultValue = "30"
                            name = "applyType"
                            onChange = {handleChange}
                            >
                            <FormControlLabel value = "quickApply" control = {<Radio />} label = "Quick Apply" />
                            <FormControlLabel value = "externalApply" control = {<Radio />} label = "External Apply" />
                        </RadioGroup>
                    </Grid>
                </Grid>
            </DialogContent>
            <DialogActions>
                <Box width="100%" 
                    display="flex" 
                    justifyContent="space-between" 
                    color="red" 
                    alignItems={"center"}>
                    <Typography variant="caption">*Required fields</Typography>
                    <Button onClick={handleSubmit} 
                            variant="contained" 
                            disableElevation color="primary"
                            disabled={loading}
                            >   
                            {loading ? (<CircularProgress color="secondary" size={22}/>)
                                     : ("Post job")}

                    </Button>
                </Box>
            </DialogActions>
        </Dialog>
    )
}

